        i0.ɵɵelementStart(75, "p", 45);
        i0.ɵɵtext(76, "\u0412\u0430\u0436\u043D\u043E, \u0447\u0442\u043E\u0431\u044B \u043D\u0430\u0443\u043A\u0430 \u0431\u044B\u043B\u0430 \u0438\u043D\u0442\u0435\u0440\u0435\u0441\u043D\u0430 \u0431\u0443\u0434\u0443\u0449\u0435\u043C\u0443 \u0443\u0447\u0435\u043D\u043E\u043C\u0443: \u0435\u0441\u0442\u0435\u0441\u0442\u0432\u0435\u043D\u043D\u044B\u0435 \u043D\u0430\u0443\u043A\u0438 \u0441\u043B\u043E\u0436\u043D\u044B \u0432 \u0438\u0437\u0443\u0447\u0435\u043D\u0438\u0438, \u0430 \u0438\u043D\u0442\u0435\u0440\u0435\u0441 \u043F\u043E\u043C\u043E\u0433\u0430\u0435\u0442 \u043F\u0440\u0435\u043E\u0434\u043E\u043B\u0435\u0432\u0430\u0442\u044C \u0441\u043B\u043E\u0436\u043D\u043E\u0441\u0442\u0438 \u0438 \u0440\u0443\u0442\u0438\u043D\u0443. \u0421\u0435\u043C\u044C\u044F \u0438 \u0448\u043A\u043E\u043B\u044C\u043D\u044B\u0435 \u043F\u0435\u0434\u0430\u0433\u043E\u0433\u0438 \u0432\u043F\u043E\u043B\u043D\u0435 \u043C\u043E\u0433\u0443\u0442 \u043F\u043E\u043C\u043E\u0447\u044C \u0440\u0430\u0437\u0432\u0438\u0442\u044C \u0442\u0430\u043A\u043E\u0439 \u0438\u043D\u0442\u0435\u0440\u0435\u0441, \u043A\u0430\u043A \u043C\u044B \u0432\u0438\u0434\u0438\u043C \u0438\u0437 \u043F\u0440\u0438\u043C\u0435\u0440\u043E\u0432 \u0433\u0435\u0440\u043E\u0438\u043D\u044C \u043C\u0430\u0442\u0435\u0440\u0438\u0430\u043B\u0430. \u041E\u0434\u043D\u0430\u043A\u043E \u043E\u0434\u043D\u043E\u0433\u043E \u0438\u043D\u0442\u0435\u0440\u0435\u0441\u0430 \u043D\u0435\u0434\u043E\u0441\u0442\u0430\u0442\u043E\u0447\u043D\u043E. \u041D\u0430\u0448\u0438 \u0441\u043E\u0431\u0435\u0441\u0435\u0434\u043D\u0438\u0446\u044B \u043E\u0442\u043C\u0435\u0447\u0430\u044E\u0442, \u0447\u0442\u043E \u0437\u0430\u043D\u044F\u0442\u0438\u044F \u0442\u043E\u0447\u043D\u044B\u043C\u0438 \u043D\u0430\u0443\u043A\u0430\u043C\u0438 \u0442\u0440\u0435\u0431\u0443\u044E\u0442 \u043E\u043F\u0440\u0435\u0434\u0435\u043B\u0435\u043D\u043D\u044B\u0445 \u043A\u0430\u0447\u0435\u0441\u0442\u0432, \u0442\u0430\u043A\u0438\u0445, \u043A\u0430\u043A \u201C\u0442\u0435\u0445\u043D\u0438\u0447\u0435\u0441\u043A\u0438\u0439 \u0441\u043A\u043B\u0430\u0434 \u0443\u043C\u0430, \u043B\u043E\u0433\u0438\u043A\u0430, \u043F\u043E\u0441\u043B\u0435\u0434\u043E\u0432\u0430\u0442\u0435\u043B\u044C\u043D\u043E\u0441\u0442\u044C\u201D (\u0414. \u0417\u0438\u043D\u0430\u0442\u0443\u043B\u0438\u043D\u0430), \u201C\u0443\u043C\u0435\u043D\u0438\u0435 \u0440\u0430\u0431\u043E\u0442\u0430\u0442\u044C \u0441\u0430\u043C\u043E\u0441\u0442\u043E\u044F\u0442\u0435\u043B\u044C\u043D\u043E, \u0443\u043C\u0435\u043D\u0438\u0435 \u043D\u0435 \u0442\u043E\u043B\u044C\u043A\u043E \u0440\u0435\u0448\u0438\u0442\u044C \u043A\u0430\u043A\u0443\u044E-\u0442\u043E \u0437\u0430\u0434\u0430\u0447\u0443, \u043D\u043E \u0438 \u0438\u043D\u043E\u0433\u0434\u0430 \u043F\u043E\u0441\u0442\u0430\u0432\u0438\u0442\u044C \u0435\u0435 \u043F\u0435\u0440\u0435\u0434 \u0441\u043E\u0431\u043E\u0439, \u0443\u0432\u0438\u0434\u0435\u0442\u044C \u0435\u0435\u201D (\u0413. \u041A\u0430\u0440\u0430\u043C\u044B\u0448\u0435\u0432\u0430), \u201C\u0443\u0441\u0438\u0434\u0447\u0438\u0432\u043E\u0441\u0442\u044C \u0438 \u0442\u0435\u0440\u043F\u0435\u043D\u0438\u0435\u201D (\u041C. \u0410\u043B\u0435\u043A\u0441\u0430\u043D\u0434\u0440\u043E\u0432\u0430). \u0412\u0430\u0436\u043D\u043E \u0437\u043D\u0430\u0442\u044C, \u0447\u0442\u043E \u0441\u043B\u043E\u0436\u043D\u043E\u0441\u0442\u044C \u043A\u0443\u0440\u0441\u043E\u0432 \u0435\u0441\u0442\u0435\u0441\u0442\u0432\u0435\u043D\u043D\u044B\u0445 \u043D\u0430\u0443\u043A, \u043F\u0440\u0435\u043F\u043E\u0434\u0430\u0432\u0430\u0435\u043C\u044B\u0445 \u0432 \u0432\u0443\u0437\u0430\u0445, \u0437\u043D\u0430\u0447\u0438\u0442\u0435\u043B\u044C\u043D\u043E \u0432\u044B\u0448\u0435 \u0448\u043A\u043E\u043B\u044C\u043D\u043E\u0433\u043E \u0443\u0440\u043E\u0432\u043D\u044F. \u0414\u0430\u0436\u0435 \u0443\u0441\u043F\u0435\u0448\u043D\u043E \u043F\u043E\u0441\u0442\u0443\u043F\u0438\u0432\u0448\u0438\u0435 \u0441\u0442\u0443\u0434\u0435\u043D\u0442\u044B \u0434\u043E\u043B\u0436\u043D\u044B \u043F\u0440\u0438\u043B\u0430\u0433\u0430\u0442\u044C \u0437\u043D\u0430\u0447\u0438\u0442\u0435\u043B\u044C\u043D\u044B\u0435 \u0443\u0441\u0438\u043B\u0438\u044F \u0434\u043B\u044F \u043F\u043E\u043D\u0438\u043C\u0430\u043D\u0438\u044F \u043F\u0440\u0435\u0434\u043C\u0435\u0442\u043E\u0432, \u0438\u0437-\u0437\u0430 \u0447\u0435\u0433\u043E \u043C\u043E\u0433\u0443\u0442 \u0440\u0430\u0437\u043E\u0447\u0430\u0440\u043E\u0432\u0430\u0442\u044C\u0441\u044F \u0432 \u0441\u0432\u043E\u0435\u043C \u0432\u044B\u0431\u043E\u0440\u0435 \u0438 \u0437\u0430\u0442\u0435\u043C \u043E\u0442\u043A\u0430\u0437\u0430\u0442\u044C\u0441\u044F \u043E\u0442 \u0440\u0430\u0431\u043E\u0442\u044B \u043F\u043E \u0441\u043F\u0435\u0446\u0438\u0430\u043B\u044C\u043D\u043E\u0441\u0442\u0438. ");
        i0.ɵɵelementEnd();
