        i0.ɵɵelement(88, "iframe", 49);
        i0.ɵɵelementStart(89, "p", 45);
        i0.ɵɵtext(90, "\u0420\u0430\u0441\u0441\u043A\u0430\u0437\u044B \u043D\u0430\u0448\u0438\u0445 \u0433\u0435\u0440\u043E\u0438\u043D\u044C \u0441\u0432\u0438\u0434\u0435\u0442\u0435\u043B\u044C\u0441\u0442\u0432\u0443\u044E\u0442, \u0447\u0442\u043E \u0435\u0441\u0442\u044C \u0438 \u043E\u0431\u0449\u0438\u0435 \u0442\u0440\u0443\u0434\u043D\u043E\u0441\u0442\u0438, \u043A\u043E\u0442\u043E\u0440\u044B\u0435 \u0432 \u043E\u0441\u043D\u043E\u0432\u043D\u043E\u043C \u0441\u0432\u044F\u0437\u0430\u043D\u044B \u0441 \u043F\u043E\u043F\u0430\u0434\u0430\u043D\u0438\u0435\u043C \u0432 \u0441\u0442\u0430\u0433\u043D\u0438\u0440\u0443\u044E\u0449\u0438\u0435 \u0441\u0444\u0435\u0440\u044B \u043D\u0430\u0443\u043A\u0438, \u0433\u0434\u0435 \u043C\u0430\u043B\u043E \u0438\u043D\u0442\u0435\u0440\u0435\u0441\u043D\u044B\u0445 \u043F\u0440\u043E\u0435\u043A\u0442\u043E\u0432 \u0438 \u043D\u0435\u0442 \u0434\u0432\u0438\u0436\u0435\u043D\u0438\u044F; \u0441 \u043D\u0435\u043E\u0431\u0445\u043E\u0434\u0438\u043C\u043E\u0441\u0442\u044C\u044E \u043F\u0435\u0440\u0435\u043A\u0432\u0430\u043B\u0438\u0444\u0438\u043A\u0430\u0446\u0438\u0438 \u043F\u0440\u0438 \u0441\u043C\u0435\u043D\u0435 \u043D\u0430\u0443\u0447\u043D\u043E\u0433\u043E \u043D\u0430\u043F\u0440\u0430\u0432\u043B\u0435\u043D\u0438\u044F \u0438\u043B\u0438 \u0441\u043F\u0435\u0446\u0438\u0430\u043B\u044C\u043D\u043E\u0441\u0442\u0438: \u043E\u0434\u043D\u043E\u0432\u0440\u0435\u043C\u0435\u043D\u043D\u043E \u0443\u0447\u0438\u0442\u044C\u0441\u044F \u0438 \u0440\u0430\u0431\u043E\u0442\u0430\u0442\u044C \u043D\u0435\u043F\u0440\u043E\u0441\u0442\u043E; \u0441 \u043D\u0435\u0431\u043E\u043B\u044C\u0448\u0438\u043C\u0438 \u0437\u0430\u0440\u043F\u043B\u0430\u0442\u0430\u043C\u0438 \u043C\u043E\u043B\u043E\u0434\u044B\u0445 \u0441\u043F\u0435\u0446\u0438\u0430\u043B\u0438\u0441\u0442\u043E\u0432. \u041D\u043E \u0435\u0441\u0442\u044C \u0438 \u0442\u0440\u0443\u0434\u043D\u043E\u0441\u0442\u0438, \u043A\u043E\u0442\u043E\u0440\u044B\u0435 \u0438\u0441\u043F\u044B\u0442\u044B\u0432\u0430\u044E\u0442 \u0442\u043E\u043B\u044C\u043A\u043E \u0436\u0435\u043D\u0449\u0438\u043D\u0430\u043C. \u0423\u0445\u043E\u0434 \u0432 \u0434\u0435\u043A\u0440\u0435\u0442 \u043E\u0437\u043D\u0430\u0447\u0430\u0435\u0442 \u043E\u0442\u0441\u0442\u0430\u0432\u0430\u043D\u0438\u0435 \u0432 \u043D\u0430\u0443\u043A\u0435. \u0412\u043E\u0437\u0432\u0440\u0430\u0449\u0430\u0442\u044C\u0441\u044F \u043C\u043E\u0436\u0435\u0442 \u0431\u044B\u0442\u044C \u0441\u043B\u043E\u0436\u043D\u044B\u043C, \u0435\u0441\u043B\u0438 \u043D\u0435 \u043F\u043E\u043C\u043E\u0433\u0443\u0442 \u0438 \u043D\u0435 \u043F\u043E\u0434\u0434\u0435\u0440\u0436\u0430\u0442 \u0440\u0443\u043A\u043E\u0432\u043E\u0434\u0441\u0442\u0432\u043E \u0438 \u043A\u043E\u043B\u043B\u0435\u0433\u0438. \u0420\u0430\u0437\u0440\u044B\u0432\u0430\u0442\u044C\u0441\u044F \u043C\u0435\u0436\u0434\u0443 \u0441\u0435\u043C\u044C\u0435\u0439 \u0438 \u043A\u0430\u0440\u044C\u0435\u0440\u043E\u0439 \u0442\u043E\u0436\u0435 \u0441\u043E\u0432\u0441\u0435\u043C \u043D\u0435 \u043F\u0440\u043E\u0441\u0442\u043E, \u0435\u0441\u043B\u0438 \u043D\u0435 \u043F\u043E\u0434\u0434\u0435\u0440\u0436\u0438\u0442 \u0441\u0435\u043C\u044C\u044F. \u0410 \u0435\u0449\u0435 \u0435\u0441\u0442\u044C \u0434\u043B\u0438\u0442\u0435\u043B\u044C\u043D\u044B\u0435 \u043A\u043E\u043C\u0430\u043D\u0434\u0438\u0440\u043E\u0432\u043A\u0438, \u043D\u043E\u0447\u043D\u044B\u0435 \u0441\u043C\u0435\u043D\u044B \u043D\u0430 \u0443\u0441\u0442\u0430\u043D\u043E\u0432\u043A\u0430\u0445 \u0438 \u043D\u043E\u0447\u043D\u044B\u0435 \u0441\u043E\u0437\u0432\u043E\u043D\u044B \u0441 \u043A\u043E\u043B\u043B\u0435\u0433\u0430\u043C\u0438 \u0438\u0437 \u043A\u043E\u043B\u043B\u0430\u0431\u043E\u0440\u0430\u0446\u0438\u0439 \u0432 \u0434\u0440\u0443\u0433\u0438\u0445 \u0447\u0430\u0441\u043E\u0432\u044B\u0445 \u043F\u043E\u044F\u0441\u0430\u0445. ");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(91, "p", 45);
        i0.ɵɵtext(92, " \u0412 \u043A\u043E\u043D\u0446\u0435 \u043F\u0440\u043E\u0448\u043B\u043E\u0433\u043E \u0432\u0435\u043A\u0430, \u043A\u043E\u0433\u0434\u0430 \u043D\u0435\u043A\u043E\u0442\u043E\u0440\u044B\u0435 \u0438\u0437 \u043D\u0430\u0448\u0438\u0445 \u0433\u0435\u0440\u043E\u0438\u043D\u044C \u043D\u0430\u0447\u0438\u043D\u0430\u043B\u0438 \u0441\u0432\u043E\u0439 \u043F\u0443\u0442\u044C \u0432 \u043D\u0430\u0443\u043A\u0435, \u043F\u0440\u043E\u0431\u043B\u0435\u043C \u0431\u044B\u043B\u043E \u0435\u0449\u0435 \u0431\u043E\u043B\u044C\u0448\u0435. \u041C\u0430\u043B\u0435\u043D\u044C\u043A\u0438\u0435 \u0437\u0430\u0440\u043F\u043B\u0430\u0442\u044B, \u043E\u0442\u0441\u0443\u0442\u0441\u0442\u0432\u0438\u0435 \u0432\u0430\u043A\u0430\u043D\u0441\u0438\u0439, \u043F\u0440\u043E\u0438\u0437\u0432\u043E\u043B \u0440\u0443\u043A\u043E\u0432\u043E\u0434\u0438\u0442\u0435\u043B\u0435\u0439. \u041E\u0434\u043D\u0430\u0436\u0434\u044B \u0418. \u0412. \u0422\u0438\u0442\u043A\u043E\u0432\u0430 \u043E\u0431\u043D\u0430\u0440\u0443\u0436\u0438\u043B\u0430, \u0447\u0442\u043E \u0435\u0435 \u0432\u044B\u043F\u043B\u0430\u0442\u044B \u043F\u043E \u0433\u0440\u0430\u043D\u0442\u0443 \u043D\u0430 \u043F\u0440\u043E\u0435\u043A\u0442 \u0432 \u0447\u0435\u0442\u044B\u0440\u0435 \u0440\u0430\u0437\u0430 \u043C\u0435\u043D\u044C\u0448\u0435, \u0447\u0435\u043C \u0443 \u0435\u0435 \u043A\u043E\u043B\u043B\u0435\u0433. \u041A \u0441\u0447\u0430\u0441\u0442\u044C\u044E, \u0432 \u0438\u0442\u043E\u0433\u0435 \u0438 \u0443 \u0418. \u0412. \u0422\u0438\u0442\u043A\u043E\u0432\u043E\u0439, \u0438 \u0443 \u0413. \u0410. \u041A\u0430\u0440\u0430\u043C\u044B\u0448\u0435\u0432\u043E\u0439, \u0442\u043E\u0436\u0435 \u0441\u0442\u043E\u043B\u043A\u043D\u0443\u0432\u0448\u0435\u0439\u0441\u044F \u043F\u043E\u043D\u0430\u0447\u0430\u043B\u0443 \u0441 \u0442\u0440\u0443\u0434\u043D\u043E\u0441\u0442\u044F\u043C\u0438, \u0432\u0441\u0451 \u043F\u043E\u043B\u0443\u0447\u0438\u043B\u043E\u0441\u044C: \u0441\u043B\u043E\u0436\u0438\u043B\u0430\u0441\u044C \u043A\u0430\u0440\u044C\u0435\u0440\u0430, \u0441\u043E\u0441\u0442\u043E\u044F\u043B\u0438\u0441\u044C \u0441\u0435\u0440\u044C\u0435\u0437\u043D\u044B\u0435 \u043F\u0440\u043E\u0435\u043A\u0442\u044B, \u043F\u043E\u043B\u0443\u0447\u0435\u043D\u044B \u0432\u0430\u0436\u043D\u044B\u0435 \u0440\u0435\u0437\u0443\u043B\u044C\u0442\u0430\u0442\u044B. \u041F\u043E\u043C\u043E\u0449\u044C \u0438 \u043F\u043E\u043D\u0438\u043C\u0430\u043D\u0438\u0435 \u0440\u0443\u043A\u043E\u0432\u043E\u0434\u0441\u0442\u0432\u0430, \u043F\u043E\u0434\u0434\u0435\u0440\u0436\u043A\u0430 \u0441\u0435\u043C\u044C\u0438, \u043B\u044E\u0431\u043E\u0432\u044C \u0438 \u0438\u043D\u0442\u0435\u0440\u0435\u0441 \u043A \u043D\u0430\u0443\u043A\u0435 - \u0432\u043E\u0442 \u0447\u0442\u043E \u043F\u043E\u043C\u043E\u0433\u043B\u043E \u043D\u0430\u0448\u0438\u043C \u0433\u0435\u0440\u043E\u0438\u043D\u044F\u043C. ");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(93, "p", 45);
        i0.ɵɵtext(94, " \u041D\u0430\u0443\u0447\u043D\u0430\u044F \u043A\u0430\u0440\u044C\u0435\u0440\u0430 \u041C\u0430\u0440\u0433\u0430\u0440\u0438\u0442\u044B \u0412\u0430\u0441\u0438\u043B\u044C\u0435\u0432\u043D\u044B \u0410\u043B\u0435\u043A\u0441\u0430\u043D\u0434\u0440\u043E\u0432\u043E\u0439 - \u043F\u0440\u0435\u043A\u0440\u0430\u0441\u043D\u044B\u0439 \u043F\u0440\u0438\u043C\u0435\u0440 \u0442\u043E\u0433\u043E, \u0447\u0442\u043E \u043C\u0435\u043D\u044F\u0442\u044C \u043D\u0430\u043F\u0440\u0430\u0432\u043B\u0435\u043D\u0438\u0435 \u0434\u0435\u044F\u0442\u0435\u043B\u044C\u043D\u043E\u0441\u0442\u0438 \u043D\u0438\u043A\u043E\u0433\u0434\u0430 \u043D\u0435 \u043F\u043E\u0437\u0434\u043D\u043E. \u0417\u0430\u043A\u043E\u043D\u0447\u0438\u043B\u0430 \u043F\u043E\u043B\u0438\u0442\u0435\u0445\u043D\u0438\u0447\u0435\u0441\u043A\u0438\u0439 \u0438\u043D\u0441\u0442\u0438\u0442\u0443\u0442, \u0440\u0430\u0431\u043E\u0442\u0430\u043B\u0430 \u0438\u043D\u0436\u0435\u043D\u0435\u0440\u043E\u043C. \u0417\u0430\u0442\u0435\u043C \u0443\u0432\u043B\u0435\u043A\u043B\u0430\u0441\u044C \u0433\u0435\u043D\u0435\u0442\u0438\u043A\u043E\u0439 \u0438 \u0432 \u0442\u0440\u0438\u0434\u0446\u0430\u0442\u044C \u043B\u0435\u0442 \u043F\u0435\u0440\u0435\u0448\u043B\u0430 \u0432 \u044D\u0442\u0443 \u0441\u0444\u0435\u0440\u0443. \u0423\u043F\u043E\u0440\u043D\u043E \u0443\u0447\u0438\u043B\u0430\u0441\u044C \u043E\u0434\u043D\u043E\u0432\u0440\u0435\u043C\u0435\u043D\u043D\u043E \u0441 \u0440\u0430\u0431\u043E\u0442\u043E\u0439. \u0417\u0430\u0449\u0438\u0442\u0438\u043B\u0430 \u043A\u0430\u043D\u0434\u0438\u0434\u0430\u0442\u0441\u043A\u0443\u044E \u0434\u0438\u0441\u0441\u0435\u0440\u0442\u0430\u0446\u0438\u044E. \u041F\u0440\u0438\u043D\u044F\u043B\u0430 \u0443\u0447\u0430\u0441\u0442\u0438\u0435 \u0432 \u043D\u0435\u0441\u043A\u043E\u043B\u044C\u043A\u0438\u0445 \u043F\u0440\u043E\u0435\u043A\u0442\u0430\u0445 \u0432 \u0433\u0435\u043D\u0435\u0442\u0438\u043A\u0435. 