        i0.ɵɵtext(115, ", \u0432\u0443\u0437\u044B, \u0434\u0430\u0432\u0430\u044F \u043E\u0441\u043D\u043E\u0432\u0443, \u0447\u0430\u0441\u0442\u043E \u00AB\u0437\u0430\u043F\u0430\u0437\u0434\u044B\u0432\u0430\u044E\u0442 \u0432\u00A0\u0441\u0440\u0430\u0432\u043D\u0435\u043D\u0438\u0438 \u0441\u00A0\u0441\u043E\u0432\u0440\u0435\u043C\u0435\u043D\u043D\u043E\u0439 \u043D\u0430\u0443\u043A\u043E\u0439, \u043A\u043E\u0442\u043E\u0440\u0430\u044F \u043F\u0440\u044F\u043C\u043E \u0441\u0435\u0439\u0447\u0430\u0441 \u0434\u0435\u043B\u0430\u0435\u0442\u0441\u044F \u0432\u00A0\u043D\u0430\u0443\u0447\u043D\u044B\u0445 \u0446\u0435\u043D\u0442\u0440\u0430\u0445\u00BB. \u00AB\u0423\u00A0\u0441\u0442\u0443\u0434\u0435\u043D\u0442\u0430, \u043A\u0430\u043A \u0438\u00A0\u0443\u00A0\u0448\u043A\u043E\u043B\u044C\u043D\u0438\u043A\u0430, \u043D\u0435\u0442 \u043F\u0440\u0430\u043A\u0442\u0438\u0447\u0435\u0441\u043A\u0438 \u043D\u0438\u043A\u0430\u043A\u043E\u0433\u043E \u043F\u043E\u043D\u0438\u043C\u0430\u043D\u0438\u044F, \u0447\u0442\u043E \u0441\u0435\u0433\u043E\u0434\u043D\u044F \u043F\u0440\u043E\u0438\u0441\u0445\u043E\u0434\u0438\u0442 \u0432\u00A0\u0438\u0441\u0441\u043B\u0435\u0434\u043E\u0432\u0430\u0442\u0435\u043B\u044C\u0441\u043A\u0438\u0445 \u0438\u043D\u0441\u0442\u0438\u0442\u0443\u0442\u0430\u0445\u00BB,\u00A0\u2014 \u0434\u043E\u0431\u0430\u0432\u043B\u044F\u0435\u0442 \u043E\u043D\u0430. ");
        i0.ɵɵelementEnd();
        i0.ɵɵelement(116, "app-photogallery", 58);
        i0.ɵɵelementStart(117, "p", 54);
        i0.ɵɵtext(118, " \u0418\u043D\u043E\u0433\u0434\u0430 \u043E\u043A\u0430\u0437\u044B\u0432\u0430\u0435\u0442\u0441\u044F, \u0447\u0442\u043E \u0432\u044B\u0431\u043E\u0440 \u043D\u0430\u043F\u0440\u0430\u0432\u043B\u0435\u043D\u0438\u044F \u043F\u0440\u0438 \u043F\u043E\u0441\u0442\u0443\u043F\u043B\u0435\u043D\u0438\u0438 \u0432\u00A0\u0432\u0443\u0437 \u0431\u044B\u043B \u043E\u0448\u0438\u0431\u043E\u0447\u043D\u044B\u043C. ");
        i0.ɵɵelementStart(119, "strong");
        i0.ɵɵtext(120, "\u0418\u0440\u0438\u043D\u0430 \u0422\u0438\u0442\u043A\u043E\u0432\u0430");
        i0.ɵɵelementEnd();
        i0.ɵɵtext(121, " \u0441\u0447\u0438\u0442\u0430\u0435\u0442, \u0447\u0442\u043E \u00AB\u043F\u043E\u043D\u044F\u0442\u044C, \u0447\u0435\u0433\u043E \u0442\u044B\u00A0\u0445\u043E\u0447\u0435\u0448\u044C, \u0434\u043E\u00A0\u0432\u0443\u0437\u0430 \u043D\u0435\u0432\u043E\u0437\u043C\u043E\u0436\u043D\u043E. \u0412\u00A0\u0441\u0435\u043C\u043D\u0430\u0434\u0446\u0430\u0442\u044C \u043B\u0435\u0442 \u043F\u043E\u043D\u044F\u0442\u044C \u044D\u0442\u043E\u0433\u043E \u043D\u0435\u043B\u044C\u0437\u044F, \u0438\u00A0\u0432\u00A0\u0432\u043E\u0441\u0435\u043C\u043D\u0430\u0434\u0446\u0430\u0442\u044C \u043D\u0435\u043B\u044C\u0437\u044F. \u0422\u043E, \u0447\u0442\u043E \u0432\u00A0\u043D\u0435\u043A\u043E\u0442\u043E\u0440\u044B\u0445 \u0441\u0442\u0440\u0430\u043D\u0430\u0445 \u0441\u043E\u0432\u0435\u0440\u0448\u0435\u043D\u043D\u043E\u043B\u0435\u0442\u0438\u0435 \u043D\u0430\u0441\u0442\u0443\u043F\u0430\u0435\u0442 \u0432\u00A0\u0434\u0432\u0430\u0434\u0446\u0430\u0442\u044C \u043E\u0434\u0438\u043D \u0433\u043E\u0434, \u0441\u043E\u0432\u0435\u0440\u0448\u0435\u043D\u043D\u043E \u043F\u0440\u0430\u0432\u0438\u043B\u044C\u043D\u043E. \u0422\u043E\u043B\u044C\u043A\u043E \u0442\u043E\u0433\u0434\u0430 \u0447\u0435\u043B\u043E\u0432\u0435\u043A \u043C\u043E\u0436\u0435\u0442 \u043D\u0430\u0447\u0430\u0442\u044C \u0437\u0430\u0434\u0443\u043C\u044B\u0432\u0430\u0442\u044C\u0441\u044F \u0438\u00A0\u0447\u0442\u043E-\u0442\u043E \u0432\u044B\u0431\u0438\u0440\u0430\u0442\u044C. \u0423\u00A0\u043C\u0435\u043D\u044F \u044D\u0442\u043E \u0432\u043E\u043E\u0431\u0449\u0435 \u043F\u043E\u043B\u0443\u0447\u0438\u043B\u043E\u0441\u044C \u0432\u00A0\u0442\u0440\u0438\u0434\u0446\u0430\u0442\u044C \u0442\u0440\u0438 \u0433\u043E\u0434\u0430\u00BB. \u041F\u0440\u0438\u0448\u043B\u043E\u0441\u044C \u0432\u043E\u0441\u043F\u043E\u043B\u043D\u044F\u0442\u044C \u043D\u0435\u0434\u043E\u0441\u0442\u0430\u044E\u0449\u0438\u0435 \u0437\u043D\u0430\u043D\u0438\u044F \u043F\u0430\u0440\u0430\u043B\u043B\u0435\u043B\u044C\u043D\u043E \u0441\u00A0\u0440\u0430\u0431\u043E\u0442\u043E\u0439. ");
        i0.ɵɵelementEnd();
        i0.ɵɵelement(122, "app-photogallery", 58);
        i0.ɵɵelementStart(123, "p", 54);
        i0.ɵɵtext(124, " \u0418\u00A0");
        i0.ɵɵelementStart(125, "strong");
        i0.ɵɵtext(126, "\u041C\u0430\u0440\u0433\u0430\u0440\u0438\u0442\u0430 \u0412\u0430\u0441\u0438\u043B\u044C\u0435\u0432\u043D\u0430 \u0410\u043B\u0435\u043A\u0441\u0430\u043D\u0434\u0440\u043E\u0432\u0430");
        i0.ɵɵelementEnd();
        i0.ɵɵtext(127, " \u043D\u0435\u00A0\u0437\u043D\u0430\u043B\u0430, \u0447\u0442\u043E \u0441\u0432\u043E\u0435 \u0441\u0430\u043C\u043E\u0435 \u0441\u0438\u043B\u044C\u043D\u043E\u0435 \u0432\u00A0\u0436\u0438\u0437\u043D\u0438 \u0443\u0432\u043B\u0435\u0447\u0435\u043D\u0438\u0435 \u043E\u043D\u0430 \u043D\u0430\u0439\u0434\u0435\u0442 \u043F\u043E\u0437\u0436\u0435 \u0438\u00A0\u0432\u00A0\u0441\u043E\u0432\u0441\u0435\u043C \u0434\u0440\u0443\u0433\u043E\u0439 \u043E\u0431\u043B\u0430\u0441\u0442\u0438. \u041E\u043A\u043E\u043D\u0447\u0438\u043B\u0430 \u043F\u043E\u043B\u0438\u0442\u0435\u0445\u043D\u0438\u0447\u0435\u0441\u043A\u0438\u0439 \u0438\u043D\u0441\u0442\u0438\u0442\u0443\u0442, \u043F\u043E\u0439\u0442\u0438 \u0432\u00A0\u043A\u043E\u0442\u043E\u0440\u044B\u0439 \u043F\u043E\u0441\u043E\u0432\u0435\u0442\u043E\u0432\u0430\u043B\u0430 \u043C\u0430\u043C\u0430, \u043F\u0440\u043E\u0440\u0430\u0431\u043E\u0442\u0430\u043B\u0430 \u043D\u0435\u0441\u043A\u043E\u043B\u044C\u043A\u043E \u043B\u0435\u0442 \u043F\u043E\u00A0\u0442\u0435\u0445\u043D\u0438\u0447\u0435\u0441\u043A\u043E\u0439 \u0441\u043F\u0435\u0446\u0438\u0430\u043B\u044C\u043D\u043E\u0441\u0442\u0438 \u0432\u00A0\u041E\u0431\u043D\u0438\u043D\u0441\u043A\u0435. \u0420\u0430\u0431\u043E\u0442\u0430\u043B\u0430 \u0445\u043E\u0440\u043E\u0448\u043E, \u043D\u043E\u00A0\u0431\u0435\u0437 \u0438\u043D\u0442\u0435\u0440\u0435\u0441\u0430. \u041F\u043E\u0442\u043E\u043C \u0447\u0435\u0440\u0435\u0437 \u043A\u043B\u0443\u0431 \u043C\u043E\u043B\u043E\u0434\u044B\u0445 \u0443\u0447\u0435\u043D\u044B\u0445 \u0443\u0432\u043B\u0435\u043A\u043B\u0430\u0441\u044C \u0433\u0435\u043D\u0435\u0442\u0438\u043A\u043E\u0439. \u041F\u0435\u0440\u0435\u0448\u043B\u0430 \u0432\u00A0\u0418\u043D\u0441\u0442\u0438\u0442\u0443\u0442 \u043C\u0435\u0434\u0438\u0446\u0438\u043D\u0441\u043A\u043E\u0439 \u0440\u0430\u0434\u0438\u043E\u043B\u043E\u0433\u0438\u0438, \u043E\u0442\u0434\u0435\u043B \u0431\u0438\u043E\u0444\u0438\u0437\u0438\u043A\u0438. \u041D\u043E\u0432\u0443\u044E \u043F\u0440\u043E\u0444\u0435\u0441\u0441\u0438\u044E \u043E\u0441\u0432\u0430\u0438\u0432\u0430\u043B\u0430 \u0441\u0430\u043C\u043E\u0441\u0442\u043E\u044F\u0442\u0435\u043B\u044C\u043D\u043E, \u043A\u043B\u0430\u0441\u0441\u0438\u0447\u0435\u0441\u043A\u043E\u0433\u043E \u0431\u0438\u043E\u043B\u043E\u0433\u0438\u0447\u0435\u0441\u043A\u043E\u0433\u043E \u043E\u0431\u0440\u0430\u0437\u043E\u0432\u0430\u043D\u0438\u044F \u043D\u0435\u00A0\u043F\u043E\u043B\u0443\u0447\u0438\u043B\u0430. \u0417\u0430\u0442\u0435\u043C \u0437\u0430\u043A\u043E\u043D\u0447\u0438\u043B\u0430 \u0430\u0441\u043F\u0438\u0440\u0430\u043D\u0442\u0443\u0440\u0443, \u0437\u0430\u0449\u0438\u0442\u0438\u043B\u0430 \u0434\u0438\u0441\u0441\u0435\u0440\u0442\u0430\u0446\u0438\u044E. ");
        i0.ɵɵelementEnd();
        i0.ɵɵelement(128, "app-photogallery", 58);
        i0.ɵɵelementStart(129, "p", 54);
        i0.ɵɵtext(130, "\u041F\u043E\u043B\u0443\u0447\u0430\u0435\u0442\u0441\u044F, \u0447\u0442\u043E \u043D\u0430\u0447\u0430\u043B\u043E \u043D\u0430\u0443\u0447\u043D\u043E\u0439 \u043A\u0430\u0440\u044C\u0435\u0440\u044B\u00A0\u2014 \u044D\u0442\u043E \u0442\u0440\u0443\u0434\u043D\u043E\u0435, \u043D\u043E\u00A0\u0438\u043D\u0442\u0435\u0440\u0435\u0441\u043D\u043E\u0435 \u0432\u0440\u0435\u043C\u044F \u043F\u043E\u0438\u0441\u043A\u0430 \u0441\u0435\u0431\u044F \u0432\u00A0\u043D\u0430\u0443\u043A\u0435. ");
        i0.ɵɵelementStart(131, "strong");
        i0.ɵɵtext(132, "\u0414\u0430\u043D\u0438\u044F \u0417\u0438\u043D\u0430\u0442\u0443\u043B\u0438\u043D\u0430");
        i0.ɵɵelementEnd();
        i0.ɵɵtext(133, " \u0441\u043E\u0432\u0435\u0442\u0443\u0435\u0442 \u00AB\u043D\u0435\u00A0\u0443\u0445\u043E\u0434\u0438\u0442\u044C \u0432\u00A0\u0443\u0437\u043A\u0438\u0435 \u043D\u0430\u043F\u0440\u0430\u0432\u043B\u0435\u043D\u0438\u044F, \u043F\u0440\u043E\u0431\u043E\u0432\u0430\u0442\u044C \u0440\u0430\u0437\u043D\u043E\u0435, \u0438\u0441\u043A\u0430\u0442\u044C \u0441\u0435\u0431\u044F. \u041F\u0440\u043E\u0431\u043E\u0432\u0430\u0442\u044C \u0438\u00A0\u043D\u0435\u00A0\u0441\u0434\u0430\u0432\u0430\u0442\u044C\u0441\u044F\u00BB.");
        i0.ɵɵelementEnd();
        i0.ɵɵelement(134, "app-photogallery", 58);
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(135, "section", 59);
        i0.ɵɵelementStart(136, "div", 44);
        i0.ɵɵelementStart(137, "h2", 60);
        i0.ɵɵtext(138, "\u0421\u0435\u043C\u044C\u044F");
        i0.ɵɵelement(139, "br");
        i0.ɵɵtext(140, "\u0438 \u0440\u0430\u0431\u043E\u0442\u0430");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(141, "div", 46);
        i0.ɵɵelementStart(142, "p", 47);
        i0.ɵɵtext(143, "\u00AB\u0411\u0435\u0437 \u0438\u0445 \u043F\u043E\u0434\u0434\u0435\u0440\u0436\u043A\u0438 \u044F \u0431\u044B \u043D\u0435 \u0441\u043F\u0440\u0430\u0432\u0438\u043B\u0430\u0441\u044C\u00BB");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(144, "p", 48);
        i0.ɵɵtext(145, "\u0421\u0435\u043C\u044C\u044F, \u043E\u0441\u043E\u0431\u0435\u043D\u043D\u043E \u0441\u00A0\u0434\u0435\u0442\u044C\u043C\u0438, \u043C\u043D\u043E\u0433\u0438\u043C \u043A\u0430\u0436\u0435\u0442\u0441\u044F \u043E\u0441\u043D\u043E\u0432\u043D\u043E\u0439 \u0442\u0440\u0443\u0434\u043D\u043E\u0441\u0442\u044C\u044E \u0434\u043B\u044F \u043F\u043E\u0441\u0442\u0440\u043E\u0435\u043D\u0438\u044F \u0443\u0441\u043F\u0435\u0448\u043D\u043E\u0439 \u043D\u0430\u0443\u0447\u043D\u043E\u0439 \u043A\u0430\u0440\u044C\u0435\u0440\u044B. \u0421\u0435\u043C\u044C\u044F \u0442\u0440\u0435\u0431\u0443\u0435\u0442 \u0432\u0440\u0435\u043C\u0435\u043D\u0438 \u0438\u00A0\u0441\u0438\u043B, \u0434\u0435\u0442\u0438 \u043C\u043E\u0433\u0443\u0442 \u0437\u0430\u0431\u043E\u043B\u0435\u0442\u044C, \u043A\u043E\u0433\u0434\u0430 \u043D\u0443\u0436\u043D\u043E \u0443\u0435\u0437\u0436\u0430\u0442\u044C \u0432\u00A0\u043A\u043E\u043C\u0430\u043D\u0434\u0438\u0440\u043E\u0432\u043A\u0443, \u0430\u00A0\u043E\u0442\u0447\u0435\u0442\u043D\u044B\u0439 \u043A\u043E\u043D\u0446\u0435\u0440\u0442 \u0445\u043E\u0440\u0430 \u043F\u0440\u0438\u0434\u0435\u0442\u0441\u044F \u043A\u0430\u043A \u0440\u0430\u0437 \u043D\u0430\u00A0\u0432\u0435\u0447\u0435\u0440\u043D\u0438\u0439 \u0441\u043E\u0437\u0432\u043E\u043D \u0441\u00A0\u0437\u0430\u0440\u0443\u0431\u0435\u0436\u043D\u044B\u043C\u0438 \u043A\u043E\u043B\u043B\u0435\u0433\u0430\u043C\u0438. \u041D\u0430\u0448\u0438 \u0433\u0435\u0440\u043E\u0438\u043D\u0438 \u043D\u0435\u00A0\u043E\u0442\u0440\u0438\u0446\u0430\u044E\u0442: \u0434\u0430, \u0441\u043E\u0447\u0435\u0442\u0430\u0442\u044C \u0441\u0435\u043C\u044C\u044E \u0438\u00A0\u043D\u0430\u0443\u043A\u0443 \u0441\u043B\u043E\u0436\u043D\u043E. \u041A\u043E\u043C\u0443-\u0442\u043E \u043F\u0440\u0438\u0445\u043E\u0434\u0438\u043B\u043E\u0441\u044C \u0440\u0430\u0431\u043E\u0442\u0430\u0442\u044C \u0437\u0430\u00A0\u043A\u043E\u043C\u043F\u044C\u044E\u0442\u0435\u0440\u043E\u043C \u0441\u00A0\u043C\u0430\u043B\u044B\u0448\u043E\u043C \u043D\u0430\u00A0\u0440\u0443\u043A\u0430\u0445, \u043A\u043E\u043C\u0443-\u0442\u043E\u00A0\u2014 \u0443\u0441\u0430\u0434\u0438\u0432 \u043E\u0434\u043D\u043E\u0433\u043E \u0440\u0435\u0431\u0435\u043D\u043A\u0430 \u0438\u0433\u0440\u0430\u0442\u044C \u0441\u043B\u0435\u0432\u0430 \u043E\u0442\u00A0\u0441\u0435\u0431\u044F, \u0430\u00A0\u0434\u0440\u0443\u0433\u043E\u0433\u043E \u0441\u043F\u0440\u0430\u0432\u0430. \u0412\u00A0\u0442\u043E\u00A0\u0436\u0435 \u0432\u0440\u0435\u043C\u044F \u0438\u043C\u0435\u043D\u043D\u043E \u0441\u0435\u043C\u044C\u044F \u043C\u043E\u0436\u0435\u0442 \u043F\u043E\u0434\u0434\u0435\u0440\u0436\u0430\u0442\u044C \u0447\u0435\u043B\u043E\u0432\u0435\u043A\u0430 \u0432\u00A0\u0442\u0440\u0443\u0434\u043D\u0443\u044E \u043C\u0438\u043D\u0443\u0442\u0443, \u0432\u043E\u043E\u0434\u0443\u0448\u0435\u0432\u0438\u0442\u044C \u0435\u0433\u043E \u043D\u0430\u00A0\u043F\u0440\u0438\u043D\u044F\u0442\u0438\u0435 \u0432\u0430\u0436\u043D\u043E\u0433\u043E \u0440\u0435\u0448\u0435\u043D\u0438\u044F. ");
        i0.ɵɵelementEnd();
