        i0.ɵɵelementStart(155, "p", 47);
        i0.ɵɵtext(156, "\u00AB\u0420\u0430\u0431\u043E\u0442\u0430, \u043A\u043E\u0442\u043E\u0440\u043E\u0439 \u0437\u0430\u043D\u0438\u043C\u0430\u0435\u0448\u044C\u0441\u044F \u0441 \u0440\u0430\u0434\u043E\u0441\u0442\u044C\u044E\u00BB");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(157, "p", 48);
        i0.ɵɵtext(158, "\u041A\u0430\u0436\u0434\u0430\u044F \u0438\u0437 \u043D\u0430\u0448\u0438\u0445 \u0441\u043E\u0431\u0435\u0441\u0435\u0434\u043D\u0438\u0446 \u0438\u0434\u0451\u0442 \u0432\u00A0\u043D\u0430\u0443\u043A\u0435 \u0441\u0432\u043E\u0438\u043C \u043F\u0443\u0442\u0435\u043C. \u0418\u00A0\u0432\u0441\u0451\u00A0\u0436\u0435 \u0438\u0445 \u0432\u0441\u0435\u0445 \u043E\u0431\u044A\u0435\u0434\u0438\u043D\u044F\u0435\u0442\u00A0\u0442\u043E, \u0447\u0442\u043E \u043E\u043D\u0438 \u043B\u044E\u0431\u044F\u0442 \u0441\u0432\u043E\u044E \u0440\u0430\u0431\u043E\u0442\u0443 \u0438\u00A0\u0434\u043E\u0431\u0438\u0432\u0430\u044E\u0442\u0441\u044F \u0432\u043F\u0435\u0447\u0430\u0442\u043B\u044F\u044E\u0449\u0438\u0445 \u0440\u0435\u0437\u0443\u043B\u044C\u0442\u0430\u0442\u043E\u0432 \u0432\u00A0\u0441\u0432\u043E\u0438\u0445 \u043E\u0431\u043B\u0430\u0441\u0442\u044F\u0445 \u0438\u0441\u0441\u043B\u0435\u0434\u043E\u0432\u0430\u043D\u0438\u0439. ");
        i0.ɵɵelementEnd();
        i0.ɵɵelementEnd();
