        i0.ɵɵelementStart(163, "h2", 66);
        i0.ɵɵtext(164, "\u0422\u0440\u0443\u0434\u043D\u043E\u0441\u0442\u0438");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(165, "div", 46);
        i0.ɵɵelementStart(166, "p", 47);
        i0.ɵɵtext(167, "\u00AB\u042D\u0442\u043E \u0431\u044B\u043B\u0438 \u0442\u043E\u0447\u043A\u0438 \u0440\u043E\u0441\u0442\u0430\u00BB");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(168, "p", 48);
        i0.ɵɵtext(169, "\u041A\u0430\u043A\u0438\u0435 \u0442\u0440\u0443\u0434\u043D\u043E\u0441\u0442\u0438 \u043C\u043E\u0433\u0443\u0442 \u0436\u0434\u0430\u0442\u044C \u0432\u00A0\u043D\u0430\u0443\u0447\u043D\u043E\u0439 \u043A\u0430\u0440\u044C\u0435\u0440\u0435? \u0415\u0441\u0442\u044C\u00A0\u043B\u0438 \u043A\u0430\u043A\u0438\u0435-\u0442\u043E \u043E\u0441\u043E\u0431\u044B\u0435 \u0441\u043B\u043E\u0436\u043D\u043E\u0441\u0442\u0438 \u0432\u00A0\u0440\u0430\u0431\u043E\u0442\u0435 \u0436\u0435\u043D\u0449\u0438\u043D-\u0443\u0447\u0451\u043D\u044B\u0445? \u041F\u043E\u0447\u0435\u043C\u0443 \u0432\u00A0\u0435\u0441\u0442\u0435\u0441\u0442\u0432\u0435\u043D\u043D\u044B\u0445 \u043D\u0430\u0443\u043A\u0430\u0445 \u0434\u043E\u043B\u044F \u0436\u0435\u043D\u0449\u0438\u043D \u0447\u0443\u0442\u044C \u0431\u043E\u043B\u044C\u0448\u0435\u00A020%? ");
        i0.ɵɵelementEnd();
        i0.ɵɵelementEnd();
        i0.ɵɵelementEnd();
        i0.ɵɵelement(170, "iframe", 67);
        i0.ɵɵelementStart(171, "p", 54);
        i0.ɵɵtext(172, "\u0420\u0430\u0441\u0441\u043A\u0430\u0437\u044B \u043D\u0430\u0448\u0438\u0445 \u0433\u0435\u0440\u043E\u0438\u043D\u044C \u0441\u0432\u0438\u0434\u0435\u0442\u0435\u043B\u044C\u0441\u0442\u0432\u0443\u044E\u0442 \u043E\u00A0\u0442\u043E\u043C, \u0447\u0442\u043E \u0435\u0441\u0442\u044C \u043E\u0431\u0449\u0438\u0435\u00A0\u2014 \u0438\u00A0\u0434\u043B\u044F \u043C\u0443\u0436\u0447\u0438\u043D, \u0438\u00A0\u0434\u043B\u044F \u0436\u0435\u043D\u0449\u0438\u043D\u00A0\u2014 \u0442\u0440\u0443\u0434\u043D\u043E\u0441\u0442\u0438, \u043A\u043E\u0442\u043E\u0440\u044B\u0435 \u0432\u00A0\u043E\u0441\u043D\u043E\u0432\u043D\u043E\u043C \u0441\u0432\u044F\u0437\u0430\u043D\u044B \u0441\u00A0\u043F\u043E\u043F\u0430\u0434\u0430\u043D\u0438\u0435\u043C \u0432\u00A0\u0441\u0442\u0430\u0433\u043D\u0438\u0440\u0443\u044E\u0449\u0438\u0435 \u0441\u0444\u0435\u0440\u044B \u043D\u0430\u0443\u043A\u0438, \u0433\u0434\u0435 \u043C\u0430\u043B\u043E \u0438\u043D\u0442\u0435\u0440\u0435\u0441\u043D\u044B\u0445 \u043F\u0440\u043E\u0435\u043A\u0442\u043E\u0432 \u0438\u00A0\u043D\u0435\u0442 \u0434\u0432\u0438\u0436\u0435\u043D\u0438\u044F; \u0441\u00A0\u043D\u0435\u043E\u0431\u0445\u043E\u0434\u0438\u043C\u043E\u0441\u0442\u044C\u044E \u043F\u0435\u0440\u0435\u043A\u0432\u0430\u043B\u0438\u0444\u0438\u043A\u0430\u0446\u0438\u0438 \u043F\u0440\u0438 \u0441\u043C\u0435\u043D\u0435 \u043D\u0430\u0443\u0447\u043D\u043E\u0433\u043E \u043D\u0430\u043F\u0440\u0430\u0432\u043B\u0435\u043D\u0438\u044F (\u0430\u00A0\u043E\u0434\u043D\u043E\u0432\u0440\u0435\u043C\u0435\u043D\u043D\u043E \u0443\u0447\u0438\u0442\u044C\u0441\u044F \u0438\u00A0\u0440\u0430\u0431\u043E\u0442\u0430\u0442\u044C \u043D\u0435\u043F\u0440\u043E\u0441\u0442\u043E); \u0441\u00A0\u043D\u0435\u0431\u043E\u043B\u044C\u0448\u043E\u0439 \u0437\u0430\u0440\u043F\u043B\u0430\u0442\u043E\u0439 \u043D\u0430\u0447\u0438\u043D\u0430\u044E\u0449\u0438\u0445 \u0443\u0447\u0451\u043D\u044B\u0445. \u0410\u00A0\u0435\u0449\u0435 \u0435\u0441\u0442\u044C \u0434\u043B\u0438\u0442\u0435\u043B\u044C\u043D\u044B\u0435 \u043A\u043E\u043C\u0430\u043D\u0434\u0438\u0440\u043E\u0432\u043A\u0438, \u043D\u043E\u0447\u043D\u044B\u0435 \u0441\u043C\u0435\u043D\u044B \u043D\u0430\u00A0\u0443\u0441\u0442\u0430\u043D\u043E\u0432\u043A\u0430\u0445 \u0438\u00A0\u043E\u043D\u043B\u0430\u0439\u043D-\u0441\u043E\u0432\u0435\u0449\u0430\u043D\u0438\u044F \u043F\u043E\u0437\u0434\u043D\u043E \u0432\u0435\u0447\u0435\u0440\u043E\u043C \u0441\u00A0\u043A\u043E\u043B\u043B\u0435\u0433\u0430\u043C\u0438 \u0438\u0437\u00A0\u043A\u043E\u043B\u043B\u0430\u0431\u043E\u0440\u0430\u0446\u0438\u0439 \u0432\u00A0\u0434\u0440\u0443\u0433\u0438\u0445 \u0447\u0430\u0441\u043E\u0432\u044B\u0445 \u043F\u043E\u044F\u0441\u0430\u0445.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(173, "p", 54);
        i0.ɵɵtext(174, "\u0415\u0441\u0442\u044C \u0442\u0430\u043A\u0436\u0435 \u043F\u0440\u043E\u0431\u043B\u0435\u043C\u044B, \u0441\u00A0\u043A\u043E\u0442\u043E\u0440\u044B\u043C\u0438 \u0441\u0442\u0430\u043B\u043A\u0438\u0432\u0430\u044E\u0442\u0441\u044F \u0442\u043E\u043B\u044C\u043A\u043E \u0436\u0435\u043D\u0449\u0438\u043D\u044B. \u041E\u041E\u041D \u0441\u0447\u0438\u0442\u0430\u0435\u0442, \u0447\u0442\u043E \u00AB\u0433\u043B\u0430\u0432\u043D\u044B\u043C\u0438 \u043F\u0440\u0435\u043F\u044F\u0442\u0441\u0442\u0432\u0438\u044F\u043C\u0438 \u0434\u043B\u044F \u0436\u0435\u043D\u0449\u0438\u043D \u0432\u00A0\u043D\u0430\u0443\u043A\u0435 \u044F\u0432\u043B\u044F\u044E\u0442\u0441\u044F \u0441\u0442\u0435\u0440\u0435\u043E\u0442\u0438\u043F\u044B \u0438\u00A0\u043F\u0440\u0435\u0434\u0443\u0431\u0435\u0436\u0434\u0435\u043D\u0438\u044F \u0432\u00A0\u043E\u0431\u0449\u0435\u0441\u0442\u0432\u0435\u00BB. \u00AB\u0417\u0430\u0447\u0435\u043C \u0442\u0435\u0431\u0435 \u044D\u0442\u0430 \u043D\u0430\u0443\u043A\u0430?\u00BB, \u00AB\u0412\u044B\u00A0\u043C\u043D\u043E\u0433\u043E \u0432\u0438\u0434\u0435\u043B\u0438 \u0436\u0435\u043D\u0449\u0438\u043D-\u0430\u043A\u0430\u0434\u0435\u043C\u0438\u043A\u043E\u0432?\u00BB, \u00AB\u0423\u0439\u0434\u0435\u0448\u044C \u0441\u043D\u0430\u0447\u0430\u043B\u0430 \u0432\u00A0\u0434\u0435\u043A\u0440\u0435\u0442, \u0430\u00A0\u043F\u043E\u0442\u043E\u043C \u0438\u00A0\u0438\u0437\u00A0\u043D\u0430\u0443\u043A\u0438\u00BB\u00A0\u2014 \u0442\u0430\u043A\u0438\u0435 \u0444\u0440\u0430\u0437\u044B \u043C\u043E\u0436\u0435\u0442 \u0443\u0441\u043B\u044B\u0448\u0430\u0442\u044C \u043B\u044E\u0431\u0430\u044F \u0434\u0435\u0432\u0443\u0448\u043A\u0430, \u0440\u0435\u0448\u0438\u0432\u0448\u0430\u044F \u0441\u0432\u044F\u0437\u0430\u0442\u044C \u0441\u0432\u043E\u044E \u0436\u0438\u0437\u043D\u044C \u0441\u00A0\u043D\u0430\u0443\u043A\u043E\u0439, \u0438\u00A0\u043E\u0442\u00A0\u0440\u043E\u0434\u0438\u0442\u0435\u043B\u0435\u0439, \u0438\u00A0\u043E\u0442\u00A0\u0437\u043D\u0430\u043A\u043E\u043C\u044B\u0445, \u0438\u00A0\u043E\u0442\u00A0\u043A\u043E\u043B\u043B\u0435\u0433. \u041A\u0441\u0442\u0430\u0442\u0438, \u0434\u0435\u0432\u0443\u0448\u0435\u043A \u043D\u0435\u00A0\u043C\u043E\u0442\u0438\u0432\u0438\u0440\u0443\u0435\u0442 \u0438\u0434\u0442\u0438 \u0432\u00A0\u043D\u0430\u0443\u043A\u0443 \u0438\u00A0\u043C\u0430\u043B\u043E\u0435 \u043A\u043E\u043B\u0438\u0447\u0435\u0441\u0442\u0432\u043E \u0436\u0435\u043D\u0449\u0438\u043D \u0441\u0440\u0435\u0434\u0438 \u0440\u0443\u043A\u043E\u0432\u043E\u0434\u0438\u0442\u0435\u043B\u0435\u0439, \u043E\u0431\u043B\u0430\u0434\u0430\u0442\u0435\u043B\u0435\u0439 \u0432\u044B\u0441\u043E\u043A\u0438\u0445 \u043D\u0430\u0443\u0447\u043D\u044B\u0445 \u0437\u0432\u0430\u043D\u0438\u0439 \u0438\u00A0\u043B\u0430\u0443\u0440\u0435\u0430\u0442\u043E\u0432 \u043F\u0440\u0435\u043C\u0438\u0439. \u0415\u0441\u043B\u0438\u00A0\u0431\u044B \u0436\u0435\u043D\u0449\u0438\u043D\u044B, \u0437\u0430\u043D\u0438\u043C\u0430\u044E\u0449\u0438\u0435 \u0442\u0430\u043A\u0438\u0435 \u0434\u043E\u043B\u0436\u043D\u043E\u0441\u0442\u0438 \u0438\u00A0\u043E\u0431\u043B\u0430\u0434\u0430\u044E\u0449\u0438\u0435 \u0442\u0430\u043A\u0438\u043C\u0438 \u0437\u0432\u0430\u043D\u0438\u044F\u043C\u0438, \u0431\u044B\u043B\u0438\u00A0\u0431\u044B \u043F\u0440\u0435\u0434\u0441\u0442\u0430\u0432\u043B\u0435\u043D\u044B \u0432\u00A0\u043D\u0430\u0443\u043A\u0435 \u0432\u00A0\u0440\u0430\u0432\u043D\u043E\u0439 \u0441\u0442\u0435\u043F\u0435\u043D\u0438 \u0441\u00A0\u043C\u0443\u0436\u0447\u0438\u043D\u0430\u043C\u0438, \u044D\u0442\u043E \u043C\u043E\u0433\u043B\u043E\u00A0\u0431\u044B \u0441\u043B\u0443\u0436\u0438\u0442\u044C \u043F\u0440\u0438\u043C\u0435\u0440\u043E\u043C \u0443\u0441\u043F\u0435\u0448\u043D\u043E\u0439 \u0441\u0430\u043C\u043E\u0440\u0435\u0430\u043B\u0438\u0437\u0430\u0446\u0438\u0438 \u0436\u0435\u043D\u0449\u0438\u043D \u0438\u00A0\u043F\u0440\u0438\u0432\u043B\u0435\u043A\u0430\u0442\u044C \u0432\u00A0\u043D\u0430\u0443\u043A\u0443 \u0431\u043E\u043B\u044C\u0448\u0435 \u0434\u0435\u0432\u0443\u0448\u0435\u043A.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(175, "p", 54);
        i0.ɵɵtext(176, "\u0415\u0441\u0442\u044C \u0443\u00A0\u0436\u0435\u043D\u0449\u0438\u043D\u044B \u0438\u00A0\u043E\u0431\u044A\u0435\u043A\u0442\u0438\u0432\u043D\u044B\u0435 \u0442\u0440\u0443\u0434\u043D\u043E\u0441\u0442\u0438. \u0423\u0445\u043E\u0434 \u0432\u00A0\u0434\u0435\u043A\u0440\u0435\u0442 \u043E\u0437\u043D\u0430\u0447\u0430\u0435\u0442 \u043E\u0442\u0441\u0442\u0430\u0432\u0430\u043D\u0438\u0435 \u0432\u00A0\u043D\u0430\u0443\u043A\u0435. \u0418\u00A0\u0435\u0441\u043B\u0438 \u0440\u0443\u043A\u043E\u0432\u043E\u0434\u0441\u0442\u0432\u043E \u0438\u00A0\u043A\u043E\u043B\u043B\u0435\u0433\u0438 \u043D\u0435\u00A0\u043F\u043E\u043C\u043E\u0433\u0443\u0442, \u0432\u043E\u0437\u0432\u0440\u0430\u0449\u0435\u043D\u0438\u0435 \u043C\u043E\u0436\u0435\u0442 \u0431\u044B\u0442\u044C \u0441\u043B\u043E\u0436\u043D\u044B\u043C. \u0418\u00A0\u0441\u043E\u0447\u0435\u0442\u0430\u0442\u044C \u0441\u0435\u043C\u044C\u044E \u0438\u00A0\u043A\u0430\u0440\u044C\u0435\u0440\u0443 \u0442\u043E\u0436\u0435 \u0441\u043E\u0432\u0441\u0435\u043C \u043D\u0435\u00A0\u043F\u0440\u043E\u0441\u0442\u043E: \u0431\u0435\u0437 \u043F\u043E\u0434\u0434\u0435\u0440\u0436\u043A\u0438 \u0441\u0435\u043C\u044C\u0438 \u043D\u0435\u00A0\u043E\u0431\u043E\u0439\u0442\u0438\u0441\u044C.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(177, "p", 54);
        i0.ɵɵtext(178, "\u0412\u00A0\u043A\u043E\u043D\u0446\u0435 \u043F\u0440\u043E\u0448\u043B\u043E\u0433\u043E \u0432\u0435\u043A\u0430, \u043A\u043E\u0433\u0434\u0430 \u043D\u0435\u043A\u043E\u0442\u043E\u0440\u044B\u0435 \u0438\u0437\u00A0\u043D\u0430\u0448\u0438\u0445 \u0433\u0435\u0440\u043E\u0438\u043D\u044C \u0442\u043E\u043B\u044C\u043A\u043E \u043D\u0430\u0447\u0438\u043D\u0430\u043B\u0438 \u0441\u0432\u043E\u0439 \u043F\u0443\u0442\u044C \u0432\u00A0\u043D\u0430\u0443\u043A\u0435, \u043F\u0440\u043E\u0431\u043B\u0435\u043C \u0431\u044B\u043B\u043E \u043D\u0435\u00A0\u043C\u0435\u043D\u044C\u0448\u0435. \u041E\u0434\u043D\u0430\u0436\u0434\u044B \u0418.\u00A0\u0412. \u0422\u0438\u0442\u043A\u043E\u0432\u0430 \u043E\u0431\u043D\u0430\u0440\u0443\u0436\u0438\u043B\u0430, \u0447\u0442\u043E \u0435\u0435\u00A0\u0432\u044B\u043F\u043B\u0430\u0442\u044B \u043F\u043E\u00A0\u0433\u0440\u0430\u043D\u0442\u0443 \u043D\u0430\u00A0\u043F\u0440\u043E\u0435\u043A\u0442 \u0432\u00A0\u0447\u0435\u0442\u044B\u0440\u0435 \u0440\u0430\u0437\u0430 \u043C\u0435\u043D\u044C\u0448\u0435, \u0447\u0435\u043C \u0443\u00A0\u0435\u0435\u00A0\u043A\u043E\u043B\u043B\u0435\u0433: \u00AB\u042F\u00A0\u0440\u0430\u0431\u043E\u0442\u0430\u043B\u0430 \u0432\u00A0\u043A\u043E\u043C\u0430\u043D\u0434\u0435, \u043A\u0430\u043A \u0432\u0441\u0435, \u0430\u00A0\u043F\u043E\u043B\u0443\u0447\u0430\u043B\u0430 \u043C\u0435\u043D\u044C\u0448\u0435\u00BB. \u0418\u0441\u043F\u0440\u0430\u0432\u0438\u0442\u044C \u0441\u0438\u0442\u0443\u0430\u0446\u0438\u044E \u0440\u0443\u043A\u043E\u0432\u043E\u0434\u0441\u0442\u0432\u043E \u043E\u0442\u043A\u0430\u0437\u0430\u043B\u043E\u0441\u044C. \u00AB\u041A\u00A0\u043C\u0443\u0436\u0447\u0438\u043D\u0435\u00A0\u0431\u044B \u0442\u0430\u043A \u043D\u0435\u00A0\u043E\u0442\u043D\u043E\u0441\u0438\u043B\u0438\u0441\u044C\u00BB,\u00A0\u2014 \u0443\u0432\u0435\u0440\u0435\u043D\u0430 \u0418\u0440\u0438\u043D\u0430 \u0412\u0438\u043A\u0442\u043E\u0440\u043E\u0432\u043D\u0430.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(179, "div", 68);
        i0.ɵɵelementStart(180, "div", 69);
        i0.ɵɵtemplate(181, AppComponent_ng_template_181_Template, 1, 1, "ng-template", 51);
        i0.ɵɵelementEnd();
        i0.ɵɵelement(182, "app-photogallery", 70);
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(183, "p", 54);
        i0.ɵɵtext(184, " \u0413.\u00A0\u0410.\u00A0\u041A\u0430\u0440\u0430\u043C\u044B\u0448\u0435\u0432\u043E\u0439 \u043F\u0440\u0438\u0448\u043B\u043E\u0441\u044C \u0438\u0434\u0442\u0438 \u043D\u0430\u00A0\u043F\u0440\u0438\u0435\u043C \u043A\u00A0\u0434\u0435\u043F\u0443\u0442\u0430\u0442\u0443, \u0447\u0442\u043E\u0431\u044B \u0434\u043E\u0431\u0438\u0442\u044C\u0441\u044F \u0432\u0430\u043A\u0430\u043D\u0441\u0438\u0438, \u043F\u043E\u043B\u043E\u0436\u0435\u043D\u043D\u043E\u0439 \u043F\u043E\u00A0\u0437\u0430\u043A\u043E\u043D\u0443 \u043E\u00A0\u043C\u043E\u043B\u043E\u0434\u044B\u0445 \u0441\u043F\u0435\u0446\u0438\u0430\u043B\u0438\u0441\u0442\u0430\u0445. \u00AB\u041D\u0443\u0436\u043D\u044B \u0437\u0430\u043A\u043E\u043D\u044B, \u0438\u00A0\u0442\u043E\u0433\u0434\u0430 \u043D\u0435\u00A0\u0431\u0443\u0434\u0435\u0442 \u043D\u0438\u043A\u0430\u043A\u043E\u0439 \u0434\u0438\u0441\u043A\u0440\u0438\u043C\u0438\u043D\u0430\u0446\u0438\u0438\u00BB,\u00A0\u2014 \u0441\u0447\u0438\u0442\u0430\u0435\u0442 \u0413\u0430\u043B\u0438\u043D\u0430 \u0410\u043D\u0430\u0442\u043E\u043B\u044C\u0435\u0432\u043D\u0430. \u0412\u0430\u0436\u043D\u043E \u0442\u0430\u043A\u0436\u0435 \u043F\u043E\u043C\u043E\u0433\u0430\u0442\u044C \u0436\u0435\u043D\u0449\u0438\u043D\u0435, \u00AB\u0441\u043E\u0437\u0434\u0430\u0432\u0430\u0442\u044C \u0443\u0441\u043B\u043E\u0432\u0438\u044F \u0434\u043B\u044F \u0432\u043E\u0437\u0432\u0440\u0430\u0449\u0435\u043D\u0438\u044F \u0438\u0437\u00A0\u0434\u0435\u043A\u0440\u0435\u0442\u043D\u043E\u0433\u043E \u043E\u0442\u043F\u0443\u0441\u043A\u0430\u00BB, \u043F\u043E\u0434\u0434\u0435\u0440\u0436\u0438\u0432\u0430\u0442\u044C\u00A0\u0435\u0435, \u043F\u043E\u043A\u0430 \u043E\u043D\u0430 \u0432\u043E\u0441\u043F\u0438\u0442\u044B\u0432\u0430\u0435\u0442 \u043C\u0430\u043B\u0435\u043D\u044C\u043A\u0438\u0445 \u0434\u0435\u0442\u0435\u0439. \u00AB\u0414\u043E\u043B\u0436\u043D\u043E \u0431\u044B\u0442\u044C \u0438\u00A0\u043A\u0430\u043A\u043E\u0435-\u0442\u043E \u0441\u043D\u0438\u0441\u0445\u043E\u0436\u0434\u0435\u043D\u0438\u0435, \u0438\u00A0\u0443\u0441\u043B\u043E\u0432\u0438\u044F\u00BB,\u00A0\u2014 \u0443\u0432\u0435\u0440\u0435\u043D\u0430 \u0413.\u00A0\u0410.\u00A0\u041A\u0430\u0440\u0430\u043C\u044B\u0448\u0435\u0432\u0430. \u0412\u00A0\u043F\u0440\u0438\u043C\u0435\u0440 \u043E\u043D\u0430 \u043F\u0440\u0438\u0432\u043E\u0434\u0438\u0442 \u043E\u043F\u044B\u0442 \u0441\u0432\u043E\u0438\u0445 \u0437\u0430\u0440\u0443\u0431\u0435\u0436\u043D\u044B\u0445 \u043A\u043E\u043B\u043B\u0435\u0433.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(185, "p", 54);
        i0.ɵɵtext(186, "\u0412\u00A0\u0440\u0435\u0437\u0443\u043B\u044C\u0442\u0430\u0442\u0435 \u0443\u00A0\u043D\u0430\u0448\u0438\u0445 \u0441\u043E\u0431\u0435\u0441\u0435\u0434\u043D\u0438\u0446 \u0432\u0441\u0435 \u043F\u043E\u043B\u0443\u0447\u0438\u043B\u043E\u0441\u044C: \u0441\u043B\u043E\u0436\u0438\u043B\u0430\u0441\u044C \u043A\u0430\u0440\u044C\u0435\u0440\u0430, \u0441\u043E\u0441\u0442\u043E\u044F\u043B\u0438\u0441\u044C \u0441\u0435\u0440\u044C\u0435\u0437\u043D\u044B\u0435 \u043F\u0440\u043E\u0435\u043A\u0442\u044B, \u0434\u043E\u0441\u0442\u0438\u0433\u043D\u0443\u0442\u044B \u0432\u0430\u0436\u043D\u044B\u0435 \u0440\u0435\u0437\u0443\u043B\u044C\u0442\u0430\u0442\u044B. \u0423\u043F\u043E\u0440\u0441\u0442\u0432\u043E, \u043B\u044E\u0431\u043E\u0432\u044C \u043A\u00A0\u043D\u0430\u0443\u043A\u0435, \u043F\u043E\u0434\u0434\u0435\u0440\u0436\u043A\u0430 \u0440\u0443\u043A\u043E\u0432\u043E\u0434\u0441\u0442\u0432\u0430, \u043F\u043E\u043C\u043E\u0449\u044C \u0441\u0435\u043C\u044C\u0438\u00A0\u2014 \u0432\u043E\u0442 \u0447\u0442\u043E \u043F\u043E\u043C\u043E\u0433\u043B\u043E \u043D\u0430\u0448\u0438\u043C \u0433\u0435\u0440\u043E\u0438\u043D\u044F\u043C.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(187, "p", 54);
        i0.ɵɵtext(188, "\u041E\u043D\u0438 \u043E\u0442\u043C\u0435\u0447\u0430\u044E\u0442, \u0447\u0442\u043E \u0432\u00A0\u041E\u0431\u044A\u0435\u0434\u0438\u043D\u0435\u043D\u043D\u043E\u043C \u0438\u043D\u0441\u0442\u0438\u0442\u0443\u0442\u0435 \u044F\u0434\u0435\u0440\u043D\u044B\u0445 \u0438\u0441\u0441\u043B\u0435\u0434\u043E\u0432\u0430\u043D\u0438\u0439 \u0441\u0435\u0439\u0447\u0430\u0441 \u0440\u0430\u0431\u043E\u0442\u0430\u0442\u044C \u0434\u043E\u0441\u0442\u0430\u0442\u043E\u0447\u043D\u043E \u043A\u043E\u043C\u0444\u043E\u0440\u0442\u043D\u043E. \u041B\u044E\u0434\u043C\u0438\u043B\u0430 \u041A\u043E\u043B\u0443\u043F\u0430\u0435\u0432\u0430 \u0433\u043E\u0432\u043E\u0440\u0438\u0442 \u043E\u0431\u00A0\u044D\u0442\u043E\u043C \u0442\u0430\u043A: \u00AB\u041E\u0442\u043D\u043E\u0448\u0435\u043D\u0438\u044F \u043C\u0435\u0436\u0434\u0443 \u043A\u043E\u043B\u043B\u0435\u0433\u0430\u043C\u0438 \u0432\u00A0\u043C\u0435\u0436\u0434\u0443\u043D\u0430\u0440\u043E\u0434\u043D\u044B\u0445 \u043A\u043E\u043B\u043B\u0430\u0431\u043E\u0440\u0430\u0446\u0438\u044F\u0445, \u0432\u00A0\u043A\u043E\u0442\u043E\u0440\u044B\u0445 \u0443\u0447\u0430\u0441\u0442\u0432\u0443\u044E\u0442 \u043D\u0430\u0448\u0438 \u0441\u043E\u0442\u0440\u0443\u0434\u043D\u0438\u043A\u0438, \u0438\u00A0\u0434\u0435\u043C\u043E\u043A\u0440\u0430\u0442\u0438\u0447\u0435\u0441\u043A\u0438\u0435 \u0442\u0440\u0430\u0434\u0438\u0446\u0438\u0438 \u0441\u043E\u0442\u0440\u0443\u0434\u043D\u0438\u0447\u0435\u0441\u0442\u0432\u0430 \u0432\u00A0\u043F\u043E\u0434\u0440\u0430\u0437\u0434\u0435\u043B\u0435\u043D\u0438\u044F\u0445 \u0418\u043D\u0441\u0442\u0438\u0442\u0443\u0442\u0430 \u0441\u043B\u0443\u0436\u0430\u0442 \u043E\u0441\u043D\u043E\u0432\u0430\u043D\u0438\u0435\u043C \u0434\u043B\u044F \u043E\u043F\u0442\u0438\u043C\u0438\u0437\u043C\u0430\u00BB.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(189, "section", 71);
        i0.ɵɵelementStart(190, "div", 44);
        i0.ɵɵelementStart(191, "h2", 60);
        i0.ɵɵtext(192, "\u0423\u0432\u043B\u0435\u0447\u0435\u043D\u0438\u044F");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(193, "div", 46);
        i0.ɵɵelementStart(194, "p", 47);
        i0.ɵɵtext(195, "\u00AB\u041D\u0430\u0443\u043A\u0430\u00A0\u2014 \u044D\u0442\u043E \u0438\u00A0\u0435\u0441\u0442\u044C \u0443\u0432\u043B\u0435\u0447\u0435\u043D\u0438\u0435\u00BB");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(196, "p", 48);
        i0.ɵɵtext(197, "\u041D\u0430\u0448\u0438 \u0433\u0435\u0440\u043E\u0438\u043D\u0438 \u0441\u0445\u043E\u0436\u0438 \u0432\u00A0\u0441\u0432\u043E\u0435\u0439 \u043B\u044E\u0431\u0432\u0438 \u043A\u00A0\u043D\u0430\u0443\u043A\u0435 \u0438\u00A0\u043F\u0440\u0435\u0434\u0430\u043D\u043D\u043E\u0441\u0442\u0438\u00A0\u0435\u0439. \u0410\u00A0\u0432\u043E\u0442 \u0432\u00A0\u0441\u0432\u043E\u0438\u0445 \u0443\u0432\u043B\u0435\u0447\u0435\u043D\u0438\u044F\u0445 \u0438\u00A0\u0445\u043E\u0431\u0431\u0438 \u043E\u043D\u0438 \u0440\u0430\u0437\u043D\u044B\u0435. \u041A\u0430\u043A\u00A0\u0436\u0435 \u043E\u043D\u0438 \u043F\u0440\u043E\u0432\u043E\u0434\u044F\u0442 \u0441\u0432\u043E\u0435 \u0441\u0432\u043E\u0431\u043E\u0434\u043D\u043E\u0435 \u0432\u0440\u0435\u043C\u044F, \u0433\u0434\u0435 \u043E\u0442\u0434\u044B\u0445\u0430\u044E\u0442 \u0438\u00A0\u043E\u0442\u043A\u0443\u0434\u0430 \u0447\u0435\u0440\u043F\u0430\u044E\u0442 \u0441\u0438\u043B\u044B?");
        i0.ɵɵelementEnd();
        i0.ɵɵelementEnd();
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(198, "div", 53);
        i0.ɵɵtemplate(199, AppComponent_div_199_Template, 10, 3, "div", 72);
        i0.ɵɵelementEnd();
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(200, "section", 73);
        i0.ɵɵelementStart(201, "div", 44);
        i0.ɵɵelementStart(202, "h2", 74);
        i0.ɵɵtext(203, "\u0418\u0434\u0435\u0430\u043B\u044C\u043D\u044B\u0439");
        i0.ɵɵelement(204, "br");
        i0.ɵɵtext(205, "\u0434\u0435\u043D\u044C");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(206, "div", 46);
        i0.ɵɵelementStart(207, "p", 47);
        i0.ɵɵtext(208, "\u00AB\u041C\u043D\u0435 \u043A\u0430\u0436\u0435\u0442\u0441\u044F, \u0447\u0442\u043E \u0443\u00A0\u043C\u0435\u043D\u044F \u043A\u0430\u0436\u0434\u044B\u0439 \u0434\u0435\u043D\u044C \u0438\u0434\u0435\u0430\u043B\u044C\u043D\u044B\u0439\u00BB");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(209, "p", 48);
        i0.ɵɵtext(210, "\u041D\u0430\u0448\u0438 \u0433\u0435\u0440\u043E\u0438\u043D\u0438 \u043C\u043D\u043E\u0433\u043E \u0440\u0430\u0431\u043E\u0442\u0430\u044E\u0442, \u043F\u043E\u044D\u0442\u043E\u043C\u0443 \u0434\u043B\u044F \u0431\u043E\u043B\u044C\u0448\u0438\u043D\u0441\u0442\u0432\u0430 \u0438\u0437\u00A0\u043D\u0438\u0445 \u0438\u0434\u0435\u0430\u043B\u044C\u043D\u044B\u0439 \u0434\u0435\u043D\u044C\u00A0\u2014 \u044D\u0442\u043E \u0442\u043E\u0442 \u0434\u0435\u043D\u044C, \u043A\u043E\u0433\u0434\u0430 \u043C\u043E\u0436\u043D\u043E \u0432\u044B\u0441\u043F\u0430\u0442\u044C\u0441\u044F, \u0432\u0441\u0442\u0430\u0442\u044C, \u043A\u043E\u0433\u0434\u0430 \u0437\u0430\u0445\u043E\u0447\u0435\u0442\u0441\u044F. \u041A\u043E\u0433\u0434\u0430 \u043C\u043E\u0436\u043D\u043E \u043D\u0438\u043A\u0443\u0434\u0430 \u043D\u0435\u00A0\u0431\u0435\u0436\u0430\u0442\u044C \u0438\u00A0\u043F\u0440\u043E\u0432\u0435\u0441\u0442\u0438 \u0434\u0435\u043D\u044C \u0441\u00A0\u0441\u0435\u043C\u044C\u0435\u0439.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(211, "p", 48);
        i0.ɵɵtext(212, "\u0420\u0430\u0431\u043E\u0442\u0443 \u043D\u0430\u0448\u0438 \u0433\u0435\u0440\u043E\u0438\u043D\u0438 \u0442\u043E\u0436\u0435 \u043E\u0447\u0435\u043D\u044C \u043B\u044E\u0431\u044F\u0442. \u041F\u043E\u044D\u0442\u043E\u043C\u0443 \u0438\u0434\u0435\u0430\u043B\u044C\u043D\u044B\u0439 \u0434\u0435\u043D\u044C \u043C\u043E\u0436\u043D\u043E \u043F\u0440\u043E\u0432\u0435\u0441\u0442\u0438 \u0438\u00A0\u043D\u0430\u00A0\u0440\u0430\u0431\u043E\u0442\u0435: \u0432\u00A0\u044D\u0442\u043E\u0442 \u0434\u0435\u043D\u044C \u043C\u043E\u0436\u0435\u0442 \u0431\u044B\u0442\u044C \u0443\u0441\u043F\u0435\u0448\u043D\u043E \u0440\u0435\u0448\u0435\u043D\u0430 \u0437\u0430\u0434\u0430\u0447\u0430, \u043A\u043E\u0442\u043E\u0440\u0430\u044F \u0434\u0430\u0432\u043D\u043E \u043D\u0435\u00A0\u043F\u043E\u043B\u0443\u0447\u0430\u043B\u0430\u0441\u044C. ");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(213, "p", 48);
        i0.ɵɵtext(214, "\u041D\u0430\u0448\u0438 \u0441\u043E\u0431\u0435\u0441\u0435\u0434\u043D\u0438\u0446\u044B \u0433\u043E\u0432\u043E\u0440\u044F\u0442 \u043E\u00A0\u0442\u043E\u043C, \u0447\u0442\u043E \u0438\u0434\u0435\u0430\u043B\u044C\u043D\u044B\u0435 \u0434\u043D\u0438 \u0432\u00A0\u0438\u0445\u00A0\u0436\u0438\u0437\u043D\u0438 \u0441\u043B\u0443\u0447\u0430\u044E\u0442\u0441\u044F. \u0418\u00A0\u044D\u0442\u043E \u043F\u0440\u0435\u043A\u0440\u0430\u0441\u043D\u043E! ");
        i0.ɵɵelementEnd();
        i0.ɵɵelementEnd();
        i0.ɵɵelementEnd();
        i0.ɵɵelement(215, "iframe", 75);
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(216, "section", 76);
        i0.ɵɵelementStart(217, "div", 77);
        i0.ɵɵelementStart(218, "h2", 78);
        i0.ɵɵtext(219, "\u041F\u043E\u0436\u0435\u043B\u0430\u043D\u0438\u044F");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(220, "div", 46);
        i0.ɵɵelementStart(221, "p", 47);
        i0.ɵɵtext(222, "\u00AB\u0418\u0434\u0442\u0438 \u0437\u0430\u00A0\u0441\u0432\u043E\u0435\u0439 \u043C\u0435\u0447\u0442\u043E\u0439!\u00BB");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(223, "p", 48);
        i0.ɵɵtext(224, "\u041A\u043E\u0433\u0434\u0430 \u0432\u0441\u0442\u0440\u0435\u0447\u0430\u0435\u0448\u044C\u0441\u044F \u0441\u00A0\u0442\u0440\u0443\u0434\u043D\u043E\u0441\u0442\u044F\u043C\u0438 \u0438\u00A0\u043D\u0435\u00A0\u0437\u043D\u0430\u0435\u0448\u044C, \u043A\u0430\u043A \u0440\u0435\u0448\u0438\u0442\u044C\u0441\u044F \u043D\u0430\u00A0\u0432\u0430\u0436\u043D\u044B\u0439 \u0448\u0430\u0433 \u0438\u043B\u0438 \u0441\u043F\u0440\u0430\u0432\u0438\u0442\u044C\u0441\u044F \u0441\u00A0\u043F\u0440\u043E\u0431\u043B\u0435\u043C\u043E\u0439, \u043C\u043E\u0436\u0435\u0442 \u043F\u043E\u043C\u043E\u0447\u044C \u043F\u0440\u0438\u043C\u0435\u0440, \u0432\u0437\u044F\u0442\u044B\u0439 \u0438\u0437\u00A0\u0436\u0438\u0437\u043D\u0438 \u0447\u0435\u043B\u043E\u0432\u0435\u043A\u0430, \u043A\u043E\u0442\u043E\u0440\u044B\u0439 \u0442\u0435\u0431\u044F \u043D\u0430\u0438\u0431\u043E\u043B\u0435\u0435 \u0432\u0434\u043E\u0445\u043D\u043E\u0432\u043B\u044F\u0435\u0442. ");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(225, "p", 48);
        i0.ɵɵtext(226, "\u041D\u0430\u0448\u0438 \u0433\u0435\u0440\u043E\u0438\u043D\u0438 \u043F\u0440\u0438\u0437\u043D\u0430\u043B\u0438\u0441\u044C, \u0447\u0442\u043E \u0442\u0430\u043A\u0438\u0435 \u043B\u044E\u0434\u0438 \u0438\u0437\u00A0\u0438\u0441\u0442\u043E\u0440\u0438\u0438 \u0438\u043B\u0438 \u0440\u0435\u0430\u043B\u044C\u043D\u043E\u0439 \u0436\u0438\u0437\u043D\u0438 \u0435\u0441\u0442\u044C \u043D\u0430\u00A0\u043F\u0440\u0438\u043C\u0435\u0442\u0435 \u0438\u00A0\u0443\u00A0\u043D\u0438\u0445. ");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(227, "p", 48);
        i0.ɵɵtext(228, "\u0410\u00A0\u0435\u0449\u0435 \u043D\u0430\u0448\u0438 \u0441\u043E\u0431\u0435\u0441\u0435\u0434\u043D\u0438\u0446\u044B \u0441\u0430\u043C\u0438 \u0434\u0430\u044E\u0442 \u0441\u043E\u0432\u0435\u0442\u044B \u0431\u0443\u0434\u0443\u0449\u0438\u043C \u0438\u00A0\u043D\u0430\u0447\u0438\u043D\u0430\u044E\u0449\u0438\u043C \u0443\u0447\u0435\u043D\u044B\u043C, \u043A\u0430\u043A \u043D\u0435\u00A0\u0440\u0430\u0437\u043E\u0447\u0430\u0440\u043E\u0432\u0430\u0442\u044C\u0441\u044F \u0432\u00A0\u043D\u0430\u0443\u043A\u0435, \u043A\u0430\u043A \u0431\u044B\u0442\u044C \u0440\u0435\u0448\u0438\u0442\u0435\u043B\u044C\u043D\u044B\u043C \u0438\u00A0\u0438\u0434\u0442\u0438 \u0437\u0430\u00A0\u0441\u0432\u043E\u0435\u0439 \u043C\u0435\u0447\u0442\u043E\u0439. ");
        i0.ɵɵelementEnd();
        i0.ɵɵelementEnd();
        i0.ɵɵelementEnd();
